<script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
<script>
// Initialize dashboard when page loads
document.addEventListener('DOMContentLoaded', function() {
    initializeMoodChart();
    initializeProgressChart();
    initializeProgressCircles();
    loadAIInsights();
});

// AI Insights Loading
async function loadAIInsights() {
    try {
        const response = await fetch('/api/dashboard/ai-insights');
        const insights = await response.json();

        if (insights.success) {
            displayAIInsights(insights.data);
        } else {
            showInsightsError();
        }
    } catch (error) {
        console.error('Error loading AI insights:', error);
        showInsightsError();
    }
}

function displayAIInsights(insights) {
    const container = document.getElementById('ai-insights-content');

    let html = '';
    insights.forEach(insight => {
        html += `
            <div class="ai-insight-card">
                <h4>${insight.title}</h4>
                <p>${insight.description}</p>
                <span class="confidence-score">Confidence: ${insight.confidence}%</span>
            </div>
        `;
    });

    container.innerHTML = html;
}

function showInsightsError() {
    const container = document.getElementById('ai-insights-content');
    container.innerHTML = `
        <div class="text-center">
            <i class="fas fa-exclamation-triangle fa-2x text-warning mb-2"></i>
            <p>Unable to load AI insights at this time.</p>
            <button class="btn btn-primary btn-sm" onclick="loadAIInsights()">Retry</button>
        </div>
    `;
}

async function refreshAIInsights() {
    const button = document.querySelector('.refresh-insights');
    const originalHtml = button.innerHTML;

    button.innerHTML = '<i class="fas fa-spinner fa-spin"></i>';
    button.disabled = true;

    try {
        const response = await fetch('/api/dashboard/ai-insights?refresh=true');
        const insights = await response.json();

        if (insights.success) {
            displayAIInsights(insights.data);
        } else {
            showInsightsError();
        }
    } catch (error) {
        console.error('Error refreshing AI insights:', error);
        showInsightsError();
    } finally {
        button.innerHTML = originalHtml;
        button.disabled = false;
    }
}

// Mood Chart
function initializeMoodChart() {
    const ctx = document.getElementById('moodChart').getContext('2d');

    // Sample mood data (replace with real data)
    const moodData = {
        labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
        datasets: [{
            label: 'Mood',
            data: [6, 7, 5, 8, 7, 9, 8],
            borderColor: '#4CAF50',
            backgroundColor: 'rgba(76, 175, 80, 0.1)',
            tension: 0.4,
            fill: true
        }]
    };

    new Chart(ctx, {
        type: 'line',
        data: moodData,
        options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
                legend: {
                    display: false
                }
            },
            scales: {
                y: {
                    beginAtZero: true,
                    max: 10,
                    grid: {
                        color: 'rgba(0, 0, 0, 0.05)'
                    }
                },
                x: {
                    grid: {
                        display: false
                    }
                }
            },
            elements: {
                point: {
                    radius: 4,
                    hoverRadius: 6
                }
            }
        }
    });
}

// Progress Chart
function initializeProgressChart() {
    const ctx = document.getElementById('progressChart').getContext('2d');

    const progressData = {
        labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4'],
        datasets: [{
            label: 'Sessions',
            data: [2, 3, 4, 3],
            backgroundColor: 'rgba(76, 175, 80, 0.7)',
            borderColor: '#4CAF50',
            borderWidth: 2
        }, {
            label: 'Goals Met',
            data: [1, 2, 3, 3],
            backgroundColor: 'rgba(102, 126, 234, 0.7)',
            borderColor: '#667eea',
            borderWidth: 2
        }]
    };

    new Chart(ctx, {
        type: 'bar',
        data: progressData,
        options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
                legend: {
                    position: 'top'
                }
            },
            scales: {
                y: {
                    beginAtZero: true,
                    grid: {
                        color: 'rgba(0, 0, 0, 0.05)'
                    }
                },
                x: {
                    grid: {
                        display: false
                    }
                }
            }
        }
    });
}

// Initialize progress circles
function initializeProgressCircles() {
    document.querySelectorAll('.progress-circle').forEach(circle => {
        const progress = circle.dataset.progress;
        const degrees = (progress / 100) * 360;
        circle.style.background = `conic-gradient(#4CAF50 0deg, #4CAF50 ${degrees}deg, #e0e0e0 ${degrees}deg)`;
    });
}

// Mood logging functions
function openMoodLogger() {
