data.forgot_password.html:
{% extends "base.html" %}

{% block title %}Reset Password - Mind Mend{% endblock %}

{% block content %}
<div class="forgot-password-container">
    <div class="forgot-password-card">
        <!-- Header -->
        <div class="forgot-password-header">
            <div class="logo-section">
                <img src="{{ url_for('static', filename='logos/mindmend_logo_4.svg') }}" alt="Mind Mend" class="logo">
                <h2>Reset Password</h2>
                <p>Enter your email address and we'll send you a link to reset your password</p>
            </div>
        </div>

        <!-- Reset Form -->
        <div class="forgot-password-form">
            {% if message %}
            <div class="success-message">
                <i class="fas fa-check-circle"></i>
                {{ message }}
            </div>
            {% endif %}

            {% if error %}
            <div class="error-message">
                <i class="fas fa-exclamation-triangle"></i>
                {{ error }}
            </div>
            {% endif %}

            <form method="POST" action="{{ url_for('forgot_password') }}">
                <div class="form-group">
                    <label for="email">Email Address</label>
                    <input type="email" id="email" name="email" required 
                           placeholder="Enter your registered email address"
                           value="{{ request.form.email or '' }}">
                    <div class="form-hint">We'll send password reset instructions to this email</div>
                </div>

                <button type="submit" class="reset-btn">
                    <i class="fas fa-paper-plane"></i>
                    Send Reset Link
                </button>
            </form>
        </div>

        <!-- Navigation Links -->
        <div class="navigation-links">
            <div class="divider">
                <span>Remember your password?</span>
            </div>
            <a href="{{ url_for('login') }}" class="back-to-login">
                <i class="fas fa-arrow-left"></i>
                Back to Sign In
            </a>
        </div>
    </div>

    <!-- Help Section -->
    <div class="help-section">
        <h3>Need Help?</h3>
        <div class="help-options">
            <div class="help-item">
                <i class="fas fa-envelope"></i>
                <div>
                    <h4>Email Support</h4>
                    <p>Contact us at support@mindmend.com</p>
                </div>
            </div>
            <div class="help-item">
                <i class="fas fa-phone"></i>
                <div>
                    <h4>Phone Support</h4>
                    <p>Call us during business hours</p>
                </div>
            </div>
            <div class="help-item crisis">
                <i class="fas fa-exclamation-triangle"></i>
                <div>
                    <h4>Crisis Support</h4>
                    <p>Lifeline Australia: 13 11 14</p>
                </div>
            </div>
        </div>
    </div>
</div>
{% endblock %}

{% block styles %}
<style>
.forgot-password-container {
    min-height: 100vh;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    display: flex;
    align-items: center;
    justify-content: center;
