    border-radius: 8px;
    margin-bottom: 1.5rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.success-message {
    background: rgba(76, 175, 80, 0.1);
    color: #4CAF50;
}

.error-message {
