data.login.html:
{% extends "base.html" %}

{% block title %}Sign In to Mind Mend{% endblock %}

{% block content %}
<div class="login-container">
    <div class="login-card">
        <!-- Logo and Header -->
        <div class="login-header">
            <div class="logo-section">
                <img src="{{ url_for('static', filename='logos/mindmend_logo_4.svg') }}" alt="Mind Mend" class="login-logo">
                <h2>Welcome Back</h2>
                <p>Sign in to continue your mental health journey</p>
            </div>
        </div>

        <!-- Login Form -->
        <div class="login-form">
            {% if error %}
            <div class="error-message">
                <i class="fas fa-exclamation-triangle"></i>
                {{ error }}
            </div>
            {% endif %}

            <form method="POST" action="{{ url_for('login') }}">
                <input type="hidden" name="csrf_token" value="{{ csrf_token }}">
                <div class="form-group">
                    <label for="email">Email Address</label>
                    <input type="email" id="email" name="email" required value="{{ request.form.email or '' }}">
                </div>

                <div class="form-group">
                    <label for="password">Password</label>
                    <div class="password-input">
                        <input type="password" id="password" name="password" required>
                        <button type="button" class="password-toggle" onclick="togglePassword()">
                            <i class="fas fa-eye" id="passwordIcon"></i>
                        </button>
                    </div>
                </div>

                <div class="form-options">
                    <label class="remember-me">
                        <input type="checkbox" name="remember">
                        <span class="checkmark"></span>
                        Remember me
                    </label>
                    <a href="{{ url_for('forgot_password') }}" class="forgot-password">Forgot password?</a>
                </div>

                <button type="submit" class="login-btn">
                    <i class="fas fa-sign-in-alt"></i>
                    Sign In
                </button>
            </form>
        </div>

        <!-- Social Login -->
        <div class="social-login">
            <div class="divider">
                <span>Or continue with</span>
            </div>
            <div class="social-buttons">
                <a href="{{ url_for('oauth.oauth_login', provider='google') }}" class="social-btn google">
                    <i class="fab fa-google"></i>
                    Google
                </a>
                <a href="{{ url_for('oauth.oauth_login', provider='facebook') }}" class="social-btn facebook" style="color: #1877f2;">
                    <i class="fab fa-facebook"></i>
                    Facebook
                </a>
            </div>
            <div class="social-buttons" style="margin-top: 0.5rem;">
                <a href="{{ url_for('oauth.oauth_login', provider='microsoft') }}" class="social-btn microsoft" style="color: #0078d4;">
                    <i class="fab fa-microsoft"></i>
                    Microsoft
                </a>
                <a href="{{ url_for('oauth.oauth_login', provider='apple') }}" class="social-btn apple">
                    <i class="fab fa-apple"></i>
                    Apple
                </a>
            </div>
        </div>

        <!-- Registration Link -->
        <div class="registration-link">
            <div class="divider">
                <span>New to Mind Mend?</span>
            </div>
            <a href="{{ url_for('register') }}" class="register-link">
                <i class="fas fa-user-plus"></i>
                Create Account
            </a>
        </div>
    </div>

    <!-- Quick Access -->
    <div class="quick-access">
        <h3>Quick Access</h3>
        <div class="quick-buttons">
            <a href="{{ url_for('onboarding') }}" class="quick-btn">
                <i class="fas fa-graduation-cap"></i>
                <span>Platform Tour</span>
            </a>
            <a href="{{ url_for('video_assess') }}" class="quick-btn">
                <i class="fas fa-video"></i>
                <span>Video Assessment</span>
            </a>
            <a href="/crisis-support" class="quick-btn crisis">
                <i class="fas fa-phone"></i>
                <span>Crisis Support</span>
            </a>
        </div>
    </div>

    <!-- Crisis Support Banner -->
    <div class="crisis-banner">
        <i class="fas fa-phone"></i>
        <span>Need immediate help? <strong>Lifeline Australia: 13 11 14</strong></span>
    </div>
</div>
{% endblock %}

