    gap: 0.5rem;
    flex-wrap: wrap;
}

.feature-tag {
    background: rgba(102, 126, 234, 0.1);
    color: #667eea;
    padding: 0.25rem 0.75rem;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 500;
}

/* Therapist Matching */
.therapist-matching {
    max-width: 500px;
    margin: 0 auto;
    text-align: center;
}

.matching-animation {
    padding: 3rem 0;
}

.matching-spinner {
    position: relative;
    width: 100px;
    height: 100px;
    margin: 0 auto 2rem;
}

.spinner-ring {
    position: absolute;
    width: 100%;
    height: 100%;
    border: 4px solid rgba(255, 255, 255, 0.3);
    border-top: 4px solid white;
    border-radius: 50%;
    animation: spin 2s linear infinite;
}

.matching-icon {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 2rem;
    background: white;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.matching-text {
    color: white;
}

.matching-text h3 {
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
    font-weight: 600;
}

.matching-text p {
    font-size: 1rem;
    opacity: 0.8;
}

.therapist-result {
    animation: fadeInUp 0.6s ease;
}

.therapist-card {
    background: rgba(255, 255, 255, 0.95);
    border-radius: 20px;
    padding: 2rem;
    display: flex;
    align-items: center;
    gap: 1.5rem;
    text-align: left;
}

.therapist-avatar {
    position: relative;
    width: 80px;
    height: 80px;
    border-radius: 50%;
    overflow: hidden;
    flex-shrink: 0;
}

.therapist-avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.avatar-fallback {
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #667eea, #764ba2);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    color: white;
}

.therapist-info h3 {
    color: #333;
    font-size: 1.4rem;
    margin-bottom: 0.5rem;
    font-weight: 600;
}

.therapist-info p {
    color: #666;
    margin-bottom: 1rem;
}

.match-score {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}

.match-percentage {
    background: linear-gradient(135deg, #4CAF50, #45a049);
    color: white;
    padding: 0.25rem 0.75rem;
    border-radius: 15px;
    font-size: 0.9rem;
    font-weight: 600;
    width: fit-content;
}

.match-reasons {
    display: flex;
    gap: 0.5rem;
    flex-wrap: wrap;
}

.match-reasons span {
    background: rgba(102, 126, 234, 0.1);
    color: #667eea;
    padding: 0.25rem 0.5rem;
    border-radius: 10px;
    font-size: 0.8rem;
}

/* Setup Summary */
.setup-summary {
    max-width: 400px;
    margin: 0 auto 2rem;
}

.summary-card {
    background: rgba(255, 255, 255, 0.95);
    border-radius: 20px;
    padding: 2rem;
    text-align: left;
}

.summary-card h3 {
    color: #333;
    margin-bottom: 1rem;
    font-size: 1.3rem;
    font-weight: 600;
}

.summary-items {
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
}

.summary-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.summary-label {
    color: #666;
    font-weight: 500;
}

.summary-value {
    color: #333;
    font-weight: 600;
}

.success-icon {
    font-size: 4rem;
    margin-bottom: 1rem;
    animation: bounce 1s ease infinite alternate;
}

/* Start Options */
.start-options {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    max-width: 400px;
    margin: 0 auto;
}

.start-button {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.75rem;
    padding: 1rem 2rem;
    border: none;
    border-radius: 25px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
}

.start-button.primary {
    background: linear-gradient(135deg, #4CAF50, #45a049);
    color: white;
    box-shadow: 0 4px 20px rgba(76, 175, 80, 0.3);
}

.start-button.primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 30px rgba(76, 175, 80, 0.4);
}

.start-button.secondary {
    background: rgba(255, 255, 255, 0.9);
    color: #667eea;
    border: 2px solid rgba(102, 126, 234, 0.3);
}

.start-button.secondary:hover {
    background: white;
    border-color: #667eea;
    transform: translateY(-2px);
}

.button-icon {
    font-size: 1.2rem;
}

/* Navigation Controls */
.navigation-controls {
    position: sticky;
    bottom: 0;
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(20px);
    border-top: 1px solid rgba(255, 255, 255, 0.2);
    padding: 1rem 2rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-btn {
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 20px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.nav-btn.primary {
    background: linear-gradient(135deg, #667eea, #764ba2);
    color: white;
}

.nav-btn.primary:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
}

.nav-btn.secondary {
    background: transparent;
    color: #666;
    border: 2px solid #ddd;
}

.nav-btn.secondary:hover:not(:disabled) {
    border-color: #667eea;
    color: #667eea;
}

.nav-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none !important;
}

/* Quick Help */
.quick-help {
    position: fixed;
    bottom: 100px;
    right: 2rem;
    z-index: 1000;
}

.help-button {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background: linear-gradient(135deg, #FF6B6B, #EE5A52);
    color: white;
    border: none;
    cursor: pointer;
    font-weight: 600;
    font-size: 1.2rem;
    box-shadow: 0 4px 20px rgba(255, 107, 107, 0.3);
    transition: all 0.3s ease;
}

.help-button:hover {
    transform: scale(1.1);
}

.help-tooltip {
    position: absolute;
    bottom: 60px;
    right: 0;
    background: rgba(0, 0, 0, 0.9);
    color: white;
    padding: 1rem;
    border-radius: 10px;
    width: 250px;
    font-size: 0.9rem;
    display: none;
    animation: fadeInUp 0.3s ease;
}

.help-tooltip::after {
    content: '';
    position: absolute;
    top: 100%;
    right: 20px;
    border: 8px solid transparent;
    border-top: 8px solid rgba(0, 0, 0, 0.9);
}

/* Crisis Banner */
.crisis-banner {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: linear-gradient(135deg, #FF6B6B, #EE5A52);
    color: white;
    padding: 0.5rem;
    text-align: center;
    font-size: 0.9rem;
    font-weight: 500;
    z-index: 200;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    transform: translateY(-100%);
    transition: transform 0.3s ease;
}

.crisis-banner.show {
    transform: translateY(0);
}

.crisis-link {
    color: white;
    text-decoration: none;
    background: rgba(255, 255, 255, 0.2);
    padding: 0.25rem 0.75rem;
    border-radius: 15px;
    font-weight: 600;
    transition: all 0.3s ease;
}

.crisis-link:hover {
    background: rgba(255, 255, 255, 0.3);
    color: white;
}

/* Animations */
@keyframes slideIn {
    from {
        opacity: 0;
        transform: translateX(50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes float {
    0%, 100% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(-20px);
    }
}

@keyframes spin {
    0% {
        transform: rotate(0deg);
    }
    100% {
        transform: rotate(360deg);
    }
}

@keyframes bounce {
    from {
        transform: scale(1);
    }
    to {
        transform: scale(1.1);
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    .progress-header {
        padding: 1rem;
        flex-direction: column;
        gap: 1rem;
    }

    .progress-container {
        max-width: 100%;
    }

    .onboarding-content {
        padding: 1rem;
    }

    .step-header h1 {
        font-size: 2.2rem;
    }

    .step-header h2 {
        font-size: 1.8rem;
    }

    .focus-grid {
        grid-template-columns: 1fr;
    }

    .session-option {
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }

    .therapist-card {
        flex-direction: column;
        text-align: center;
    }

    .navigation-controls {
        padding: 1rem;
    }

    .quick-help {
        bottom: 80px;
        right: 1rem;
    }

    .start-options {
        gap: 0.75rem;
    }
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
    .focus-card,
    .session-option,
    .therapist-card,
    .summary-card {
        background: rgba(255, 255, 255, 0.1);
        color: white;
    }

    .focus-card h3,
    .session-details h3,
    .therapist-info h3,
    .summary-card h3 {
        color: white;
    }

    .focus-card p,
    .session-details p,
    .therapist-info p {
        color: rgba(255, 255, 255, 0.8);
    }
}
</style>
{% endblock %}

{% block scripts %}
<script>
// Modern Onboarding JavaScript
class ModernOnboarding {
    constructor() {
        this.currentStep = 1;
        this.totalSteps = 5;
        this.userPreferences = {
            focus: null,
            sessionType: null,
            therapist: null
        };
        this.init();
    }

    init() {
        this.bindEvents();
        this.updateProgress();
        this.showCrisisBannerIfNeeded();
    }

    bindEvents() {
        // Focus cards
        document.querySelectorAll('.focus-card').forEach(card => {
            card.addEventListener('click', (e) => this.selectFocus(e));
        });

        // Session options
        document.querySelectorAll('.session-option').forEach(option => {
            option.addEventListener('click', (e) => this.selectSession(e));
        });

        // Keyboard navigation
        document.addEventListener('keydown', (e) => {
            if (e.key === 'ArrowRight' || e.key === ' ') {
                e.preventDefault();
                this.nextStep();
            } else if (e.key === 'ArrowLeft') {
                e.preventDefault();
                this.previousStep();
            } else if (e.key === 'Escape') {
                this.skipOnboarding();
            }
        });

        // Auto-advance when selections are made
        this.setupAutoAdvance();
    }

    selectFocus(e) {
        const card = e.currentTarget;
        const focus = card.dataset.focus;

        // Remove previous selection
        document.querySelectorAll('.focus-card').forEach(c => c.classList.remove('selected'));

        // Add selection to clicked card
        card.classList.add('selected');

        // Store preference
        this.userPreferences.focus = focus;

        // Auto-advance after short delay
        setTimeout(() => {
            this.nextStep();
        }, 800);
    }

    selectSession(e) {
        const option = e.currentTarget;
        const session = option.dataset.session;

        // Remove previous selection
        document.querySelectorAll('.session-option').forEach(o => o.classList.remove('selected'));

        // Add selection to clicked option
        option.classList.add('selected');

        // Store preference
        this.userPreferences.sessionType = session;

        // Auto-advance after short delay
        setTimeout(() => {
            this.nextStep();
        }, 800);
    }

    setupAutoAdvance() {
        // Advanced to step 4 automatically triggers matching
        document.addEventListener('stepChanged', (e) => {
            if (e.detail.step === 4) {
                this.startTherapistMatching();
            }
        });
    }

    startTherapistMatching() {
        const matchingText = document.getElementById('matchingText');
        const matchingSubtext = document.getElementById('matchingSubtext');
        const matchingAnimation = document.getElementById('matchingAnimation');
        const therapistResult = document.getElementById('therapistResult');

        const stages = [
            { text: 'Analyzing your preferences...', subtext: 'Processing your focus area and session type' },
            { text: 'Finding compatible therapists...', subtext: 'Searching our database of AI specialists' },
            { text: 'Calculating match scores...', subtext: 'Using advanced algorithms to find your perfect match' },
            { text: 'Perfect match found!', subtext: 'We found an AI therapist that\'s 95% compatible with you' }
        ];

        let currentStage = 0;

        const updateStage = () => {
            if (currentStage < stages.length - 1) {
                matchingText.textContent = stages[currentStage].text;
                matchingSubtext.textContent = stages[currentStage].subtext;
                currentStage++;
                setTimeout(updateStage, 1500);
            } else {
                // Show final stage and then reveal result
                matchingText.textContent = stages[currentStage].text;
                matchingSubtext.textContent = stages[currentStage].subtext;

                setTimeout(() => {
                    matchingAnimation.style.display = 'none';
                    therapistResult.style.display = 'block';
                    this.customizeTherapistMatch();

                    // Auto-advance to final step
                    setTimeout(() => {
                        this.nextStep();
                    }, 2000);
                }, 1000);
            }
        };

        updateStage();
    }

    customizeTherapistMatch() {
        const therapistName = document.getElementById('therapistName');
        const therapistSpecialty = document.getElementById('therapistSpecialty');
        const matchReasons = document.getElementById('matchReasons');

        // Customize based on user preferences
        const therapists = {
            anxiety: {
                name: 'Dr. Maya Chen',
                specialty: 'Cognitive Behavioral Therapy Specialist',
                reasons: ['Specializes in anxiety', 'CBT approach', 'Individual therapy expert']
            },
            depression: {
                name: 'Dr. James Wilson',
                specialty: 'Depression & Mood Disorder Expert',
                reasons: ['Depression specialist', 'DBT training', 'Supportive approach']
            },
            relationships: {
                name: 'Dr. Sarah Martinez',
                specialty: 'Relationship & Couples Counselor',
                reasons: ['Gottman Method trained', 'Couples specialist', 'Communication expert']
            },
            trauma: {
                name: 'Dr. Michael Thompson',
                specialty: 'Trauma & PTSD Specialist',
                reasons: ['EMDR certified', 'Trauma-informed', 'PTSD expert']
            },
            general: {
                name: 'Dr. Lisa Rodriguez',
                specialty: 'General Mental Health & Wellness',
                reasons: ['Holistic approach', 'Wellness focused', 'Preventive care']
            },
            crisis: {
                name: 'Dr. David Kim',
                specialty: 'Crisis Intervention Specialist',
                reasons: ['Crisis certified', '24/7 available', 'Emergency trained']
            }
        };

        const selectedTherapist = therapists[this.userPreferences.focus] || therapists.general;

        therapistName.textContent = selectedTherapist.name;
        therapistSpecialty.textContent = selectedTherapist.specialty;

        matchReasons.innerHTML = '';
        selectedTherapist.reasons.forEach(reason => {
            const span = document.createElement('span');
            span.textContent = reason;
            matchReasons.appendChild(span);
        });

        // Store therapist preference
        this.userPreferences.therapist = selectedTherapist.name;
    }

    nextStep() {
        if (this.currentStep < this.totalSteps) {
            this.currentStep++;
            this.showStep(this.currentStep);
        }
    }

    previousStep() {
        if (this.currentStep > 1) {
            this.currentStep--;
            this.showStep(this.currentStep);
        }
    }

    showStep(stepNumber) {
        // Hide all steps
        document.querySelectorAll('.step-panel').forEach(panel => {
            panel.classList.remove('active');
        });

        // Show current step
        const currentPanel = document.querySelector(`[data-step="${stepNumber}"]`);
        if (currentPanel) {
            currentPanel.classList.add('active');
        }

        // Update navigation
        this.updateNavigation();
        this.updateProgress();

        // Update summary if on final step
        if (stepNumber === 5) {
            this.updateSummary();
        }

        // Dispatch custom event
        document.dispatchEvent(new CustomEvent('stepChanged', {
            detail: { step: stepNumber }
        }));
    }

    updateNavigation() {
        const backButton = document.getElementById('backButton');
        const nextButton = document.getElementById('nextButton');

        backButton.disabled = this.currentStep === 1;

        if (this.currentStep === this.totalSteps) {
            nextButton.style.display = 'none';
        } else {
            nextButton.style.display = 'flex';

            // Update button text based on step
            const buttonTexts = {
                1: 'Get Started ',
                2: 'Continue ',
                3: 'Continue ',
                4: 'Continue '
            };

            nextButton.querySelector('span').textContent = buttonTexts[this.currentStep] || 'Continue ';
        }
    }

    updateProgress() {
        const progressFill = document.getElementById('progressFill');
        const stepCounter = document.getElementById('stepCounter');

        const percentage = (this.currentStep / this.totalSteps) * 100;
        progressFill.style.width = `${percentage}%`;
        stepCounter.textContent = `${this.currentStep} of ${this.totalSteps}`;
    }

    updateSummary() {
        const focusMap = {
            anxiety: 'Anxiety & Stress',
            depression: 'Depression & Mood',
            relationships: 'Relationships',
            trauma: 'Trauma & PTSD',
            general: 'General Wellness',
            crisis: 'Crisis Support'
        };

        const sessionMap = {
            individual: 'Individual Therapy',
            couples: 'Couples Therapy',
            group: 'Group Support'
        };

        document.getElementById('selectedFocus').textContent = 
            focusMap[this.userPreferences.focus] || 'Not selected';
        document.getElementById('selectedSession').textContent = 
            sessionMap[this.userPreferences.sessionType] || 'Not selected';
        document.getElementById('selectedTherapist').textContent = 
            this.userPreferences.therapist || 'Not assigned';
    }

    showCrisisBannerIfNeeded() {
        // Show crisis banner if user selected crisis or if it's after hours
        const hour = new Date().getHours();
        const isAfterHours = hour < 8 || hour > 18;

        if (this.userPreferences.focus === 'crisis' || isAfterHours) {
            const banner = document.querySelector('.crisis-banner');
            banner.classList.add('show');
        }
    }

    skipOnboarding() {
        if (confirm('Are you sure you want to skip the setup? You can always complete it later from your settings.')) {
            this.savePreferences();
            window.location.href = '/';
        }
    }

    savePreferences() {
        // Save user preferences to localStorage
        localStorage.setItem('mindmend_onboarding_completed', 'true');
        localStorage.setItem('mindmend_user_preferences', JSON.stringify(this.userPreferences));

        // Send to backend if user is logged in
        if (window.user_id) {
            fetch('/api/user/preferences', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(this.userPreferences)
            }).catch(err => console.warn('Failed to save preferences:', err));
        }
    }

    complete() {
        this.savePreferences();

        // Show success message
        this.showSuccessMessage();
    }

    showSuccessMessage() {
        const content = document.getElementById('onboardingContent');
        content.innerHTML = `
            <div class="completion-screen">
                <div class="success-animation">
                    <div class="checkmark"></div>
                </div>
                <h2>Welcome to MindMend!</h2>
                <p>Your personalized mental health journey starts now.</p>
                <div class="completion-actions">
                    <button class="start-button primary" onclick="window.location.href='/individual'">
                        <span class="button-icon"></span>
                        Start My First Session
                    </button>
                </div>
            </div>
        `;

        // Hide navigation
        document.querySelector('.navigation-controls').style.display = 'none';
    }
}

// Global functions
function skipOnboarding() {
    onboarding.skipOnboarding();
}

function nextStep() {
    onboarding.nextStep();
}

function previousStep() {
    onboarding.previousStep();
}

function startFirstSession() {
    onboarding.savePreferences();
    window.location.href = '/individual';
}

function takeTour() {
    onboarding.savePreferences();
    window.location.href = '/?tour=true';
}

function toggleHelp() {
    const tooltip = document.getElementById('helpTooltip');
    tooltip.style.display = tooltip.style.display === 'block' ? 'none' : 'block';
}

function showCrisisModal() {
    // Implement crisis modal
    alert('Crisis support coming soon. For immediate help, call Lifeline Australia: 13 11 14');
}

// Initialize when DOM is loaded
let onboarding;
document.addEventListener('DOMContentLoaded', function() {
    onboarding = new ModernOnboarding();

    // Auto-focus on first interactive element
    const firstFocusCard = document.querySelector('.focus-card');
    if (firstFocusCard && !window.matchMedia('(max-width: 768px)').matches) {
        firstFocusCard.focus();
    }
});

// Analytics tracking (placeholder)
function trackOnboardingEvent(event, data = {}) {
    if (window.gtag) {
        gtag('event', event, {
            event_category: 'onboarding',
            ...data
        });
    }
}
</script>
{% endblock %}

data.register.html:
{% extends "base.html" %}

{% block title %}Join Mind Mend - Create Your Account{% endblock %}

{% block content %}
<div class="registration-container">
    <div class="registration-card">
        <!-- Logo and Header -->
        <div class="registration-header">
            <div class="logo-section">
                <img src="{{ url_for('static', filename='logos/mindmend_logo_4.svg') }}" alt="Mind Mend" class="registration-logo">
                <h2>Join Mind Mend</h2>
                <p>Start your mental health journey with AI-powered therapy</p>
            </div>
        </div>

        <!-- Registration Form -->
        <div class="registration-form">
            <form id="registrationForm" method="POST" action="{{ url_for('register') }}">
                <input type="hidden" name="csrf_token" value="{{ csrf_token }}">
                <!-- Step 1: Basic Information -->
                <div class="form-step active" data-step="1">
                    <h3>Basic Information</h3>
                    <div class="form-group">
                        <label for="firstName">First Name</label>
                        <input type="text" id="firstName" name="firstName" required>
                        <div class="form-hint">Your first name helps us personalize your experience</div>
                    </div>

                    <div class="form-group">
                        <label for="lastName">Last Name</label>
                        <input type="text" id="lastName" name="lastName" required>
                    </div>

                    <div class="form-group">
                        <label for="email">Email Address</label>
                        <input type="email" id="email" name="email" required>
                        <div class="form-hint">We'll use this for secure login and important updates</div>
                    </div>
                </div>

                <!-- Step 2: Secure Login -->
                <div class="form-step" data-step="2">
                    <h3>Secure Login</h3>
                    <div class="form-group">
                        <label for="password">Password</label>
                        <input type="password" id="password" name="password" required minlength="8">
                        <div class="password-requirements">
                            <div class="requirement" id="length"> At least 8 characters</div>
                            <div class="requirement" id="uppercase"> One uppercase letter</div>
                            <div class="requirement" id="lowercase"> One lowercase letter</div>
                            <div class="requirement" id="number"> One number</div>
                        </div>
                    </div>

                    <div class="form-group">
                        <label for="confirmPassword">Confirm Password</label>
                        <input type="password" id="confirmPassword" name="confirmPassword" required>
                        <div class="form-hint" id="passwordMatch">Passwords must match</div>
                    </div>
                </div>

                <!-- Step 3: Personal Profile -->
                <div class="form-step" data-step="3">
                    <h3>Personal Profile</h3>
                    <div class="form-group">
                        <label for="dateOfBirth">Date of Birth</label>
                        <input type="date" id="dateOfBirth" name="dateOfBirth" required>
                        <div class="form-hint">Required to ensure age-appropriate care</div>
                    </div>

                    <div class="form-group">
                        <label for="gender">Gender (Optional)</label>
                        <select id="gender" name="gender">
                            <option value="">Prefer not to say</option>
                            <option value="female">Female</option>
                            <option value="male">Male</option>
                            <option value="non-binary">Non-binary</option>
                            <option value="other">Other</option>
                        </select>
                    </div>

                    <div class="form-group">
                        <label for="location">Location (Optional)</label>
                        <select id="location" name="location">
                            <option value="">Select your location</option>
                            <option value="US">United States</option>
                            <option value="CA">Canada</option>
                            <option value="UK">United Kingdom</option>
                            <option value="AU">Australia</option>
                            <option value="NZ">New Zealand</option>
                            <option value="DE">Germany</option>
                            <option value="FR">France</option>
                            <option value="other">Other</option>
                        </select>
                        <div class="form-hint">Helps us provide relevant resources and crisis support</div>
                    </div>
                </div>

                <!-- Step 4: Mental Health Goals -->
                <div class="form-step" data-step="4">
                    <h3>Your Mental Health Goals</h3>
                    <div class="form-group">
                        <label>What brings you to Mind Mend? (Select all that apply)</label>
                        <div class="checkbox-group">
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="anxiety">
                                <span class="checkmark"></span>
                                Managing anxiety
                            </label>
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="depression">
                                <span class="checkmark"></span>
                                Dealing with depression
                            </label>
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="stress">
                                <span class="checkmark"></span>
                                Stress management
                            </label>
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="relationships">
                                <span class="checkmark"></span>
                                Relationship issues
                            </label>
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="self-esteem">
                                <span class="checkmark"></span>
                                Building self-esteem
                            </label>
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="trauma">
                                <span class="checkmark"></span>
                                Processing trauma
                            </label>
                            <label class="checkbox-item">
                                <input type="checkbox" name="goals" value="general">
                                <span class="checkmark"></span>
                                General mental wellness
                            </label>
                        </div>
                    </div>
                </div>

                <!-- Step 5: Privacy & Consent -->
                <div class="form-step" data-step="5">
                    <h3>Privacy & Consent</h3>
                    <div class="privacy-notice">
                        <h4>Your Privacy Matters</h4>
                        <p>Mind Mend is committed to protecting your privacy and maintaining the highest standards of data security.</p>

                        <div class="privacy-highlights">
                            <div class="privacy-item">
                                <i class="fas fa-shield-alt"></i>
                                <span>All data is encrypted and stored securely</span>
                            </div>
                            <div class="privacy-item">
                                <i class="fas fa-user-lock"></i>
                                <span>Your conversations remain private</span>
                            </div>
                            <div class="privacy-item">
                                <i class="fas fa-globe-americas"></i>
                                <span>Data stored securely with global compliance</span>
                            </div>
                        </div>
                    </div>

                    <div class="consent-checkboxes">
                        <label class="checkbox-item required">
                            <input type="checkbox" name="termsAccepted" required>
                            <span class="checkmark"></span>
                            I agree to the <a href="/terms" target="_blank">Terms of Service</a> and <a href="/privacy" target="_blank">Privacy Policy</a>
                        </label>

                        <label class="checkbox-item required">
                            <input type="checkbox" name="ageConfirmed" required>
                            <span class="checkmark"></span>
                            I confirm that I am 18+ years old or have parental consent
                        </label>

                        <label class="checkbox-item">
                            <input type="checkbox" name="marketingConsent">
                            <span class="checkmark"></span>
                            Send me helpful mental health tips and platform updates (optional)
                        </label>
                    </div>
                </div>

                <!-- Navigation Buttons -->
                <div class="form-navigation">
                    <button type="button" id="prevBtn" onclick="previousStep()" disabled>
                        <i class="fas fa-chevron-left"></i> Previous
                    </button>

                    <div class="step-indicator">
                        <span class="step-dot active" data-step="1"></span>
                        <span class="step-dot" data-step="2"></span>
                        <span class="step-dot" data-step="3"></span>
                        <span class="step-dot" data-step="4"></span>
                        <span class="step-dot" data-step="5"></span>
                    </div>

                    <button type="button" id="nextBtn" onclick="nextStep()">
                        Next <i class="fas fa-chevron-right"></i>
                    </button>
                </div>
            </form>
        </div>

        <!-- Alternative Login -->
        <div class="alternative-login">
            <div class="divider">
                <span>Already have an account?</span>
            </div>
            <a href="{{ url_for('login') }}" class="login-link">
                <i class="fas fa-sign-in-alt"></i> Sign In
            </a>
        </div>
    </div>

    <!-- Crisis Support Banner -->
    <div class="crisis-banner">
        <i class="fas fa-phone"></i>
        <span>Need immediate help? <strong>Lifeline Australia: 13 11 14</strong></span>
    </div>
</div>
{% endblock %}

