{% block scripts %}
<script>
let currentStep = 1;
const totalSteps = 5;

function showStep(stepNumber) {
    // Hide all steps
    document.querySelectorAll('.form-step').forEach(step => {
        step.classList.remove('active');
    });

    // Show current step
    const currentStepElement = document.querySelector(`[data-step="${stepNumber}"]`);
    if (currentStepElement) {
        currentStepElement.classList.add('active');
    }

    // Update step indicators
    document.querySelectorAll('.step-dot').forEach((dot, index) => {
        dot.classList.remove('active', 'completed');
        if (index + 1 === stepNumber) {
            dot.classList.add('active');
        } else if (index + 1 < stepNumber) {
            dot.classList.add('completed');
        }
    });

    // Update navigation buttons
    const prevBtn = document.getElementById('prevBtn');
    const nextBtn = document.getElementById('nextBtn');

    prevBtn.disabled = stepNumber === 1;

    if (stepNumber === totalSteps) {
        nextBtn.textContent = 'Create Account';
        nextBtn.innerHTML = 'Create Account <i class="fas fa-user-plus"></i>';
        nextBtn.onclick = submitForm;
    } else {
        nextBtn.innerHTML = 'Next <i class="fas fa-chevron-right"></i>';
        nextBtn.onclick = nextStep;
    }
}

function nextStep() {
    if (validateCurrentStep()) {
        if (currentStep < totalSteps) {
            currentStep++;
            showStep(currentStep);
        }
    }
}

function previousStep() {
    if (currentStep > 1) {
        currentStep--;
        showStep(currentStep);
    }
}

function validateCurrentStep() {
    const currentStepElement = document.querySelector(`[data-step="${currentStep}"]`);
    const requiredFields = currentStepElement.querySelectorAll('input[required], select[required]');
    let isValid = true;

    requiredFields.forEach(field => {
        if (!field.value.trim()) {
            field.style.borderColor = '#f44336';
            isValid = false;
        } else {
            field.style.borderColor = '#e0e0e0';
        }
    });

    // Special validation for step 2 (password)
    if (currentStep === 2) {
        const password = document.getElementById('password').value;
        const confirmPassword = document.getElementById('confirmPassword').value;

        if (password !== confirmPassword) {
            document.getElementById('confirmPassword').style.borderColor = '#f44336';
            document.getElementById('passwordMatch').style.color = '#f44336';
            document.getElementById('passwordMatch').textContent = 'Passwords do not match';
            isValid = false;
        } else {
            document.getElementById('confirmPassword').style.borderColor = '#4CAF50';
            document.getElementById('passwordMatch').style.color = '#4CAF50';
            document.getElementById('passwordMatch').textContent = 'Passwords match';
        }
    }

    // Special validation for step 5 (required checkboxes)
    if (currentStep === 5) {
        const requiredCheckboxes = currentStepElement.querySelectorAll('input[type="checkbox"][required]');
        requiredCheckboxes.forEach(checkbox => {
            if (!checkbox.checked) {
                checkbox.parentElement.style.borderColor = '#f44336';
                isValid = false;
            } else {
                checkbox.parentElement.style.borderColor = 'transparent';
            }
        });
    }

    return isValid;
}

function submitForm() {
    if (validateCurrentStep()) {
        document.getElementById('registrationForm').submit();
    }
}

// Password strength validation
document.getElementById('password').addEventListener('input', function() {
    const password = this.value;
    const requirements = {
        length: password.length >= 8,
        uppercase: /[A-Z]/.test(password),
        lowercase: /[a-z]/.test(password),
        number: /\d/.test(password)
    };

    Object.keys(requirements).forEach(req => {
        const element = document.getElementById(req);
        if (requirements[req]) {
            element.classList.add('valid');
            element.classList.remove('invalid');
        } else {
            element.classList.add('invalid');
            element.classList.remove('valid');
        }
    });
});

// Age validation
document.getElementById('dateOfBirth').addEventListener('change', function() {
    const birthDate = new Date(this.value);
    const today = new Date();
    const age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();

    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
        age--;
    }

    if (age < 13) {
        this.style.borderColor = '#f44336';
        alert('Mind Mend is designed for users 13 and older. If you are under 18, please ensure you have parental consent.');
    } else {
        this.style.borderColor = '#4CAF50';
    }
});

// Initialize form
document.addEventListener('DOMContentLoaded', function() {
    showStep(1);

    // Add step dot click handlers
    document.querySelectorAll('.step-dot').forEach((dot, index) => {
        dot.addEventListener('click', function() {
            if (index + 1 <= currentStep || validateStepsUpTo(index + 1)) {
                currentStep = index + 1;
                showStep(currentStep);
            }
        });
    });
});

function validateStepsUpTo(stepNumber) {
    for (let i = 1; i < stepNumber; i++) {
        const stepElement = document.querySelector(`[data-step="${i}"]`);
        const requiredFields = stepElement.querySelectorAll('input[required], select[required]');

        for (let field of requiredFields) {
            if (!field.value.trim()) {
                return false;
            }
        }
    }
    return true;
}
</script>
{% endblock %}

data.session.html:
{% extends "base.html" %}

{% block title %}Therapy Session - Mind Mend{% endblock %}

{% block content %}
<div class="row">
    <div class="col-lg-8 mx-auto">
        <div class="card">
            <div class="card-header">
                <h3><i class="fas fa-comments me-2"></i>Therapy Session</h3>
            </div>
            <div class="card-body">
                {% if error %}
                <div class="alert alert-danger">
                    <i class="fas fa-exclamation-circle me-2"></i>{{ error }}
                </div>
                {% endif %}

                {% if not ai_response %}
                <!-- Session Input Form -->
                <form method="post" id="sessionForm">
                    <div class="mb-3">
                        <label for="name" class="form-label">Your Name</label>
                        <input type="text" class="form-control" id="name" name="name" required>
                    </div>

                    <div class="mb-3">
                        <label for="session_type" class="form-label">Session Type</label>
                        <select class="form-select" id="session_type" name="session_type">
                            <option value="individual">Individual Therapy</option>
                            <option value="couple">Couple Therapy</option>
                            <option value="group">Group Therapy</option>
                        </select>
                    </div>

                    <div class="mb-3">
                        <label for="user_input" class="form-label">What's on your mind?</label>
                        <textarea class="form-control" id="user_input" name="user_input" rows="5" 
                                placeholder="Share your thoughts, feelings, or concerns..." required></textarea>
                        <div class="form-text">Your input will be analyzed by AI for personalized support.</div>
                    </div>

                    <div class="d-grid gap-2">
                        <button type="submit" class="btn btn-primary btn-lg">
                            <i class="fas fa-paper-plane me-2"></i>Send Message
                        </button>
                        <button type="button" class="btn btn-outline-info" onclick="startChatSession()">
                            <i class="fas fa-comments me-2"></i>Start Live Chat
                        </button>
                    </div>
                </form>
                {% else %}
                <!-- Session Results -->
                <div class="session-results">
                    <div class="alert alert-info">
                        <h5><i class="fas fa-user me-2"></i>Session Summary</h5>
                        <p><strong>Name:</strong> {{ name }}</p>
                        <p><strong>Session Type:</strong> {{ session_type.title() }}</p>
                    </div>

                    <!-- User Input -->
                    <div class="card mb-3">
                        <div class="card-header">
                            <h6><i class="fas fa-comment me-2"></i>Your Message</h6>
                        </div>
                        <div class="card-body">
                            <p>{{ user_input }}</p>
                        </div>
                    </div>

                    <!-- AI Response -->
                    <div class="card mb-3 border-primary">
                        <div class="card-header bg-primary text-white">
                            <h6><i class="fas fa-robot me-2"></i>AI Therapist Response</h6>
                        </div>
                        <div class="card-body">
                            <p>{{ ai_response }}</p>
                        </div>
                    </div>
                    </div>

                    <!-- Alerts Section -->
                    {% if alerts %}
                    <div class="card mb-3 border-warning">
                        <div class="card-header bg-warning text-dark">
                            <h6><i class="fas fa-exclamation-triangle me-2"></i>Health & Safety Alerts</h6>
                        </div>
                        <div class="card-body">
                            {% for alert in alerts %}
                            <div class="alert alert-{{ 'danger' if alert.severity == 'critical' else 'warning' if alert.severity == 'high' else 'info' }} mb-2">
                                <strong>{{ alert.message }}</strong>
                                {% if alert.resources %}
                                <div class="mt-2">
                                    <small><strong>Resources:</strong></small>
                                    <ul class="mb-0">
                                        {% for resource in alert.resources %}
                                        <li><small>{{ resource.name }}: {{ resource.contact }}</small></li>
                                        {% endfor %}
                                    </ul>
                                </div>
                                {% endif %}
                            </div>
                            {% endfor %}
                        </div>
                    </div>
                    {% endif %}

                    <!-- Action Buttons -->
                    <div class="d-flex gap-2 flex-wrap">
                        <a href="{{ url_for('session') }}" class="btn btn-primary">
                            <i class="fas fa-plus me-2"></i>New Session
                        </a>
                        <a href="{{ url_for('video_assessment') }}" class="btn btn-info">
                            <i class="fas fa-video me-2"></i>Video Assessment
                        </a>
                        <a href="{{ url_for('dashboard') }}" class="btn btn-secondary">
                            <i class="fas fa-chart-line me-2"></i>View Progress
                        </a>
                    </div>
                </div>
                {% endif %}
            </div>
        </div>
    </div>
</div>

<!-- Real-time Analysis Panel (Hidden by default) -->
<div class="row mt-4" id="realTimePanel" style="display: none;">
    <div class="col-lg-8 mx-auto">
        <div class="card border-info">
            <div class="card-header bg-info text-white">
                <h6><i class="fas fa-analytics me-2"></i>Real-time Analysis</h6>
            </div>
            <div class="card-body">
                <div id="realTimeResults">
                    <!-- Real-time analysis results will be displayed here -->
                </div>
            </div>
        </div>
    </div>
</div>
{% endblock %}

{% block scripts %}
<script>
document.addEventListener('DOMContentLoaded', function() {
    const userInput = document.getElementById('user_input');
    const realTimePanel = document.getElementById('realTimePanel');
    const realTimeResults = document.getElementById('realTimeResults');
    let analysisTimeout;

    // Real-time text analysis
    if (userInput) {
        userInput.addEventListener('input', function() {
            clearTimeout(analysisTimeout);

            const text = this.value.trim();
            if (text.length > 50) { // Only analyze if sufficient text
                analysisTimeout = setTimeout(() => {
                    analyzeText(text);
                }, 1000); // Debounce for 1 second
            }
        });
    }

    function analyzeText(text) {
        fetch('/api/analyze-text', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                text: text,
                session_type: document.getElementById('session_type')?.value || 'individual'
            })
        })
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                console.error('Analysis error:', data.error);
                realTimePanel.style.display = 'none';
                return;
            }

            // Show real-time analysis
            realTimePanel.style.display = 'block';

            // Display analysis results
            realTimeResults.innerHTML = `
                <div class="row">
                    <div class="col-md-6">
                        <h6><i class="fas fa-brain me-2"></i>Emotional Analysis</h6>
                        <p><strong>Primary Emotion:</strong> ${data.ai_response?.emotional_state || 'Analyzing...'}</p>
                        <p><strong>Confidence:</strong> ${data.ai_response?.confidence || 'N/A'}%</p>
                    </div>
                    <div class="col-md-6">
                        <h6><i class="fas fa-shield-alt me-2"></i>Risk Assessment</h6>
                        <p><strong>Level:</strong> <span class="badge bg-${data.alerts?.length > 0 ? 'warning' : 'success'}">${data.alerts?.length > 0 ? 'Moderate' : 'Low'}</span></p>
                    </div>
                </div>
            `;
        })
        .catch(error => {
            console.error('Real-time analysis error:', error);
            realTimePanel.style.display = 'none';
        });
    }

    // Chat functionality for modern GPT interface
    window.startChatSession = function() {
        const chatContainer = document.createElement('div');
        chatContainer.className = 'chat-interface mt-4';
        chatContainer.innerHTML = `
            <div class="card">
                <div class="card-header bg-primary text-white">
                    <h6><i class="fas fa-comments me-2"></i>Live Chat with AI Therapist</h6>
                </div>
                <div class="card-body" style="height: 400px; overflow-y: auto;" id="chatMessages">
                    <div class="text-muted text-center p-3">
                        <i class="fas fa-robot fa-2x mb-2"></i>
                        <p>AI Therapist is ready to chat. How are you feeling today?</p>
                    </div>
                </div>
                <div class="card-footer">
                    <div class="input-group">
                        <input type="text" class="form-control" id="chatInput" placeholder="Type your message...">
                        <button class="btn btn-primary" onclick="sendChatMessage()">
                            <i class="fas fa-paper-plane"></i>
                        </button>
                    </div>
                </div>
            </div>
        `;

        const formContainer = document.querySelector('.session-results') || document.querySelector('.card-body');
        formContainer.appendChild(chatContainer);

        // Focus on chat input
        document.getElementById('chatInput').focus();
    };

    window.sendChatMessage = function() {
        const input = document.getElementById('chatInput');
        const messages = document.getElementById('chatMessages');
        const message = input.value.trim();

        if (!message) return;

        // Add user message
        addChatMessage('user', message);
        input.value = '';

        // Show typing indicator
        const typingId = addChatMessage('ai', '<i class="fas fa-spinner fa-spin"></i> Typing...');

        // Send to API
        fetch('/api/session', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                message: message,
                session_type: 'individual',
                name: 'Chat User'
            })
        })
        .then(response => response.json())
        .then(data => {
            // Remove typing indicator
            document.getElementById(typingId).remove();

            if (data.success) {
                addChatMessage('ai', data.response);
            } else {
                addChatMessage('ai', 'I apologize, but I\'m having technical difficulties. Please try again.');
            }
        })
        .catch(error => {
            console.error('Chat error:', error);
            document.getElementById(typingId).remove();
            addChatMessage('ai', 'I\'m sorry, I\'m having connection issues. Please try again.');
        });
    };

    function addChatMessage(sender, message) {
        const messages = document.getElementById('chatMessages');
        const messageId = 'msg_' + Date.now();
        const messageDiv = document.createElement('div');
        messageDiv.id = messageId;
        messageDiv.className = `mb-3 ${sender === 'user' ? 'text-end' : ''}`;

        messageDiv.innerHTML = `
            <div class="d-inline-block p-3 rounded ${sender === 'user' ? 'bg-primary text-white' : 'bg-light'}" style="max-width: 70%;">
                <div class="small mb-1">
                    <i class="fas fa-${sender === 'user' ? 'user' : 'robot'} me-1"></i>
                    ${sender === 'user' ? 'You' : 'AI Therapist'}
                </div>
                <div>${message}</div>
            </div>
        `;

        messages.appendChild(messageDiv);
        messages.scrollTop = messages.scrollHeight;

        return messageId;
    }

    // Allow Enter key to send messages
    document.addEventListener('keypress', function(e) {
        if (e.target.id === 'chatInput' && e.key === 'Enter') {
            sendChatMessage();
        }
    });

    function displayRealTimeAnalysis(data) {
        let html = '';

        // AI Response Preview
        if (data.ai_response && typeof data.ai_response === 'object') {
            html += '<div class="mb-3">';
            html += '<h6><i class="fas fa-brain me-2"></i>AI Insights</h6>';
            html += `<p><strong>Emotional State:</strong> ${data.ai_response.emotional_state || 'Analyzing...'}</p>`;
            if (data.ai_response.key_themes && data.ai_response.key_themes.length > 0) {
                html += '<p><strong>Key Themes:</strong> ' + data.ai_response.key_themes.join(', ') + '</p>';
            }
            html += '</div>';
        }

        // Alerts
        if (data.alerts && data.alerts.length > 0) {
            html += '<div class="mb-3">';
            html += '<h6><i class="fas fa-exclamation-triangle me-2 text-warning"></i>Alerts</h6>';
            data.alerts.forEach(alert => {
                const alertClass = alert.severity === 'critical' ? 'danger' : 
                                 alert.severity === 'high' ? 'warning' : 'info';
                html += `<div class="alert alert-${alertClass} py-2 mb-2">${alert.message}</div>`;
            });
            html += '</div>';
        }

        // Exercises
        if (data.exercises && data.exercises.length > 0) {
            html += '<div class="mb-3">';
            html += '<h6><i class="fas fa-dumbbell me-2"></i>Suggested Exercises</h6>';
            data.exercises.slice(0, 2).forEach(exercise => {
                html += `<div class="card card-body py-2 mb-2">`;
                html += `<strong>${exercise.title}</strong> - ${exercise.description}`;
                html += `</div>`;
            });
            html += '</div>';
        }

        realTimeResults.innerHTML = html || '<p class="text-muted">Analyzing your input...</p>';
    }

    // Form enhancement
    const sessionForm = document.getElementById('sessionForm');
    if (sessionForm) {
        sessionForm.addEventListener('submit', function(e) {
            const submitBtn = this.querySelector('button[type="submit"]');
            submitBtn.innerHTML = '<i class="fas fa-spinner fa-spin me-2"></i>Processing...';
            submitBtn.disabled = true;
        });
    }
});
</script>
{% endblock %}

data.video_assessment.html:
{% extends "base.html" %}

{% block title %}AI Video Assessment - Mind Mend{% endblock %}

{% block head %}
<style>
    .video-assessment-container {
        position: relative;
        background: #000;
        border-radius: 12px;
        overflow: hidden;
        box-shadow: 0 4px 20px rgba(0,0,0,0.3);
        transition: all 0.3s ease;
    }

    /* Mobile-friendly camera view */
    .camera-container {
        position: relative;
        width: 100%;
        height: 400px;
        transition: all 0.3s ease;
    }

    .camera-container.minimized {
        position: fixed;
        top: 20px;
        right: 20px;
        width: 200px;
        height: 150px;
        z-index: 1000;
        border: 2px solid #fff;
        border-radius: 12px;
        box-shadow: 0 4px 20px rgba(0,0,0,0.5);
    }

    .video-element {
        width: 100%;
        height: 100%;
        object-fit: cover;
        border-radius: 10px;
    }

    .camera-controls {
        position: absolute;
        bottom: 10px;
        left: 50%;
        transform: translateX(-50%);
        display: flex;
        gap: 10px;
    }

    .camera-controls.minimized {
        bottom: 5px;
        gap: 5px;
    }

    .camera-btn {
        padding: 8px 12px;
        border: none;
        border-radius: 20px;
        background: rgba(255,255,255,0.9);
        color: #333;
        font-size: 12px;
        cursor: pointer;
        transition: all 0.2s ease;
    }

    .camera-btn.minimized {
        padding: 4px 8px;
        font-size: 10px;
    }

    .camera-btn:hover {
        background: rgba(255,255,255,1);
        transform: scale(1.05);
    }

    /* Questions panel - always visible on mobile */
    .questions-panel {
        background: rgba(255,255,255,0.95);
        border-radius: 12px;
        padding: 20px;
        margin-top: 20px;
        min-height: 300px;
    }

    .questions-panel.camera-active {
        margin-top: 0;
    }

    /* Mobile-specific styles */
    @media (max-width: 768px) {
        .camera-container {
            height: 300px;
        }

        .camera-container.minimized {
            width: 150px;
            height: 100px;
            top: 10px;
            right: 10px;
        }

        .questions-panel {
            padding: 15px;
            margin-bottom: 120px; /* Space for minimized camera */
        }

        .questions-panel.camera-active {
            padding-top: 60px; /* Space for minimized camera when active */
        }
    }

    .assessment-overlay {
        position: absolute;
        top: 0;
        right: 0;
        background: linear-gradient(45deg, rgba(0,0,0,0.8), rgba(0,0,0,0.6));
        color: white;
        padding: 15px;
        border-radius: 0 12px 0 12px;
        min-width: 200px;
    }
    .emotion-indicator {
        display: inline-block;
        width: 16px;
        height: 16px;
        border-radius: 50%;
        margin-right: 8px;
    }
    .confidence-bar {
        height: 6px;
        background: rgba(255,255,255,0.3);
        border-radius: 3px;
        overflow: hidden;
        margin-top: 4px;
    }
    .confidence-fill {
        height: 100%;
        background: linear-gradient(90deg, #ff6b6b, #4ecdc4, #45b7d1);
        transition: width 0.3s ease;
    }
    .microexpression-alert {
        position: absolute;
        bottom: 20px;
        left: 20px;
        background: rgba(255, 193, 7, 0.9);
        color: #000;
        padding: 8px 12px;
        border-radius: 6px;
        font-size: 0.9em;
        transform: translateY(100px);
        opacity: 0;
        transition: all 0.3s ease;
    }
    .microexpression-alert.show {
        transform: translateY(0);
        opacity: 1;
    }
    .assessment-phase {
        border-left: 4px solid var(--bs-primary);
        background: rgba(var(--bs-primary-rgb), 0.1);
    }
    .assessment-complete {
        border-left: 4px solid var(--bs-success);
        background: rgba(var(--bs-success-rgb), 0.1);
    }
</style>
{% endblock %}

{% block content %}
